return dbConn;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
// HL7 formate yyyy/MM/ddTHH:mm:ss
function DateFHIRToHl7(dateInFhir) {

	channelMap.put('dateInFhir',dateInFhir);
	channelMap.put('year',dateInFhir.substring(0,4));
	channelMap.put('MM',dateInFhir.substring(5,7));
	channelMap.put('dd',dateInFhir.substring(8,10));
	channelMap.put('HH',dateInFhir.substring(11,13));
	channelMap.put('mm',dateInFhir.substring(14,16));
	channelMap.put('ss',dateInFhir.substring(17,19));


	var HL7date =new Date(dateInFhir.substring(0,4),
	dateInFhir.substring(5,7),
	dateInFhir.substring(8,10),
	dateInFhir.substring(11,13),
	dateInFhir.substring(14,16),
	dateInFhir.substring(17,19)).getTime();


    return HL7date;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
// HL7 formate yyyyMMddHHmmss
function DateHL7ToFHIR(DateHL7) {

	var FHIRdate = '';
    FHIRdate += DateHL7.substring(0,4) + '/';
    FHIRdate += DateHL7.substring(4,6) + '/';
   FHIRdate += DateHL7.substring(6,8) + 'T';

   if(DateHL7.substring(8,10)){
   FHIRdate += DateHL7.substring(8,10) + ':';
   }else {FHIRdate +='00:';}

     if(DateHL7.substring(10,12)){
   FHIRdate += DateHL7.substring(8,10) + ':';
   }else {FHIRdate +='00:';}


     if(DateHL7.substring(12,14)){
   FHIRdate += DateHL7.substring(8,10) + ':';
   }else {FHIRdate +='00';}



    FHIRdate += '+GMT';
    return FHIRdate;
}

function processRDResponse(senderRefReceived){
	var endingPosOfHeadId = senderRefReceived.indexOf("episodeId:");
	var headIdForSenderReference = senderRefReceived.substring(7, endingPosOfHeadId);
	var endingPosOfEpisodeId = senderRefReceived.indexOf("originalMessage:");
	var episodeIdForSenderReference = senderRefReceived.substring(endingPosOfHeadId+10, endingPosOfEpisodeId);
	var senderReferenceOrg = senderRefReceived.substring(endingPosOfEpisodeId+16);
	logger.debug("Channel 6 and Poll : Episode ID:  "+episodeIdForSenderReference);	
	var text = getTEXTFromBEFUND_TEXTE(headIdForSenderReference, episodeIdForSenderReference, "KB");
	if(!(input.report == "" || input.report == null)){
		text = text +"\n"+ input.report;
		logger.debug("Channel 6_D text from BEFUND_TEXTE: "+text);
		updateTEXTInBEFUND_TEXTE(headIdForSenderReference, episodeIdForSenderReference, text, "KB"); //Shoudl be KB
		updateDetailsBEFUND_KOPF(headIdForSenderReference, episodeIdForSenderReference, "CM");		
	}

}


