	logger.debug("Channel 6 and Poll : function updateDetailsBEFUND_KOPF headId:"+headId+" episodeId:"+episodeId+" befundStatus:"+befundStatus);
	var DBCONToUpdateTEXT = getFireBirdConnection();
	try{
		var DBCONToUpdateTEXT = getFireBirdConnection();
		var SQLToUpdateTEXT = "Update BEFUND_KOPF set STATUS_DRUCK='', STATUS_DATEI='N' where LABOR_NUMMER="+episodeId+" and ID="+headId+" and BEFUND_STATUS='"+befundStatus+"';";
		logger.debug("Channel 6 and Poll : Query executed to update the BEFUND_KOPF: "+SQLToUpdateTEXT);
		var res_TEXT_List = DBCONToUpdateTEXT.executeUpdate(SQLToUpdateTEXT);
		DBCONToUpdateTEXT.close();
	}catch(ex){
		logger.error("Error in executing the update statement:"+SQLToUpdateTEXT);
		DBCONToUpdateTEXT.close();
		throw ex;
	}
}

/*
	The function updateTEXTInBEFUND_TEXTE updates the table BEFUND_TEXTE or insert's a new row to it.
*/
function updateTEXTInBEFUND_TEXTE(headId, episodeId, text, text_art){
	var DBCONToUpdateTEXT = getFireBirdConnection();
	try{
		var DBCONToUpdateTEXT = getFireBirdConnection();
		var SQLToUpdateTEXT = "update BEFUND_TEXTE set TEXT = '"+text+"' where LABOR_NUMMER="+episodeId+" and BEFKOPFID="+headId+" and TEXT_ART='"+text_art+"';";
		logger.debug("Channel 6 and Poll : Query executed to update the updateTEXTInBEFUND_TEXTE: "+SQLToUpdateTEXT);
		var resOfUpdateTEXT = DBCONToUpdateTEXT.executeUpdate(SQLToUpdateTEXT);
		logger.debug("Channel 6 and Poll : Value of the result s"+resOfUpdateTEXT);
		DBCONToUpdateTEXT.close();	
		DBCONToUpdateTEXT = getFireBirdConnection();
		if(resOfUpdateTEXT == 0){ 
			var SQLToInsertTEXT = "INSERT INTO BEFUND_TEXTE (BEFKOPFID, LABOR_NUMMER, LFDNR_AUFTRAG, PROBE_ID, TEXT_ART, TEXT) VALUES ("+headId+","+episodeId+", Null , Null, 'KB', '"+text+"');";
			logger.debug("Channel 6 and Poll : Query executed to update the Insert into BEFUND_TEXTE: "+SQLToInsertTEXT);
			var resOfInserteTEXT = DBCONToUpdateTEXT.executeUpdate(SQLToInsertTEXT);
			logger.debug("Channel 6 and Poll : Value of the result s"+resOfInserteTEXT);	
		}
		DBCONToUpdateTEXT.close();	

	}catch(ex){
		logger.error(" Error in executing the update statement:"+SQLToUpdateTEXT);
		DBCONToUpdateTEXT.close();
		throw ex;
	}
}

/*
	Generic function to get a new FireBird Connection. 
*/
function getFireBirdConnection(){
	var FbDriver =  configurationMap.get("FbDriver");
	var FbUrl  = configurationMap.get("FbUrl");
	var FbUsername = configurationMap.get("FbUsername");
	var FbPw  = configurationMap.get("FbPw");

	var DBCON = DatabaseConnectionFactory.createDatabaseConnection(FbDriver,FbUrl,FbUsername,FbPw); 
	return DBCON;
}

/*
	The function getTEXTFromBEFUND_TEXTE gets the value of TEXT from the table BEFUND_TEXTE.
*/
function getTEXTFromBEFUND_TEXTE(headId, episodeId, text_art){
	var text = "";
	var DBCONToGetTEXT = getFireBirdConnection();
	try{
		var SQLToGetTEXT = "select TEXT from BEFUND_TEXTE where LABOR_NUMMER="+episodeId+" and BEFKOPFID="+headId+" and TEXT_ART='"+text_art+"';";
		logger.debug("Channel 6 and Poll : Query executed to get the TEXT: "+SQLToGetTEXT);
		var res_TEXT_List = DBCONToGetTEXT.executeCachedQuery(SQLToGetTEXT);
		var size_TEXT_List = res_TEXT_List.size();
		if (size_TEXT_List >0){
			for(var i = 0;i<size_TEXT_List;i++){
				res_TEXT_List.next();
				text = res_TEXT_List.getString("TEXT");
			}
		}
		DBCONToGetTEXT.close();	
	}catch(ex){
		logger.error("Error in executing the get TEXT statement:"+SQLToGetTEXT);
		DBCONToGetTEXT.close();	
		throw ex;
	}
	return text;	
}

/*

	See issue: http://www.mirthcorp.com/community/issues/browse/MIRTH-625

	Author: Nick Rupley
	Date Modified: 4/18/2012

	fixHL7NodeOrder: Returns a new E4X node where the order of all siblings and descendants have been fixed as per the Mirth HL7 dot notation convention.

	Arguments
	---------
		Required
		--------
		node: The node to be fixed. 
*/
function fixHL7NodeOrder(node) {
	// Create output node
	var newNode = new XML();
	// In case the node is an XMLList of multiple siblings, loop through each sibling
	for each (sibling in node) {
		// Create new sibling node
		var newSibling = new XML('<'+sibling.name().toString()+'/>');
		// Iterate through each child node
		for each (child in sibling.children())
			// If the child has its own children, then recursively fix the node order of the child
			if (child.hasComplexContent())
				newSibling.appendChild(fixHL7NodeOrder(child));
			// If the child doesn't have its own children, then just add the child to the new sibling node
			else
				newSibling.appendChild(child);
		// After recursively fixing all of the child nodes, now we'll fix the current node
		newNode += sortHL7Node(newSibling);
	}
	// Return the fixed node
	return newNode;
}

function sortHL7Node(node) {
	// If the node has no children, then there's nothing to sort
	if (node.hasSimpleContent())
		return node;
	// Create new output node
	var newNode = new XML('<'+node.name().toString()+'/>');
	// Iterate through each child in the node
	for each (child in node.children()) {
		// If the child has a QName, then we can sort on it
		if (child.name()) {
			// Get the current "index" of the child. Id est, if the QName is PID.3.1, then the index is 1
			curChildIndex = parseInt(child.name().toString().substring(child.name().toString().lastIndexOf('.')+1),10);
			// Boolean placeholder
			var inserted = false;
			// Iterate through each child currently in the NEW node
			for (var i = 0; i <= newNode.children().length()-1; i++) {
				// Get the index of the child of the new node
				loopChildIndex = parseInt(newNode.child(i).name().toString().substring(newNode.child(i).name().toString().lastIndexOf('.')+1),10);
				// If the child we want to insert has a lower index then the current child of the new node, then we're going to insert the child 
				// right before the current newNode child
				if (curChildIndex < loopChildIndex) {
					// Insert the child
					newNode.insertChildBefore(newNode.children()[i],child);
					// Set our flag, indicating that an insertion was made
					inserted = true;
					// No need to continue iteration
					break;
				}
			}
			// If no insertion was made, then the index of the child we want to insert is greater than or equal to all of the
			// indices of the children that have already been inserted in newNode. So, we'll just append the child to the end.
			if (!inserted)
				newNode.appendChild(child);
		}
	}
	// Return the sorted HL7 node
	return newNode;
}

