				tmpCopyPhysicianNameList           = sqlResult.getString("CopyPhysicianNameList");
				tmpPanelIndicator                  = sqlResult.getString("PanelIndicator");
				tmpPanelID                         = sqlResult.getString("PanelID");
				tmpPanelName                       = sqlResult.getString("PanelName");
				tmpSampleID                        = sqlResult.getString("SampleID");
				tmpSampleFluidType                 = sqlResult.getString("SampleFluidType");
				tmpSampleOriginLocation            = sqlResult.getString("SampleOriginLocation");
				tmpSampleOriginSubLocation         = sqlResult.getString("SampleOriginSubLocation");
				tmpSampleCollectionDateTime        = sqlResult.getString("SampleCollectionDateTime");
				tmpSampleCollectionMethod          = sqlResult.getString("SampleCollectionMethod");
				tmpTestSiteSampleReceivedDateTime  = sqlResult.getString("TestSiteSampleReceivedDateTime");
				tmpTestPriority                    = sqlResult.getString("TestPriority");
				tmpTestDepartment                  = sqlResult.getString("TestDepartment");
				tmpTestInstrument                  = sqlResult.getString("TestInstrument");
				tmpTestRequestSite                 = sqlResult.getString("TestRequestSite");
				tmpTestSite                        = sqlResult.getString("TestSite");
				tmpTestType                        = sqlResult.getString("TestType");
