				tmpTestResultFirstReviewedDateTime = sqlResult.getString("TestResultFirstReviewedDateTime");
				tmpTestResultReleasedDateTime      = sqlResult.getString("TestResultReleasedDateTime");
				tmpEpisodeDate		= DateUtil.getDate("yyyy-MM-dd HH:mm:ss", tmpEpisodeDateTime).getTime();

				// Check whether we moved onto a different episode, if we did and it hadn't already been processed then send a new
				// case...
				if ((tmpEpisodeID != currEpisodeID)) {
					if ($('cases_from_most_recent_episode_only') != 'true') {
						if (currEpisodeID != "") {
							if (currStatus == "N") {
								var currPatient = {"caseId": currEpisodeID , site: "", episodes: []};
								currPatient.episodes = episodes;
								// We're putting the batchSeq into the JSON message, the destination channel can split this out for use later
								// This is used for updating the records once the records are successfully sent to the web service
								var currMessage = {"batchSeq": batchSeq, "episodeID": currEpisodeID, Patient: currPatient};

								var json = JSON.stringify(currPatient);
								logger.trace("JSON Routing to REST CLIENT: " + json);
								// Send the JSON Object to the Web Service channel
								resp = router.routeMessage("Rest Client", JSON.stringify(currMessage));
								if (resp) {
									logger.trace("Response from REST Channel: " + resp.getMessage());
								}
							}
						}
					}

					episodeSize = episodes.push({episodeDate: tmpEpisodeDate,
											episodeId: tmpEpisodeID,
											attributeWithValues: [{externalName: "PID", name: "PID", value: tmpPatientID},
															{externalName: "BIRTHDATE", name: "BIRTHDATE", value: tmpPatientDOB},
