															{externalName: "SampleFluidType", name: "SampleFluidType", value: tmpSampleFluidType},
															{externalName: "SampleOriginLocation", name: "SampleOriginLocation", value: tmpSampleOriginLocation},
															{externalName: "SampleOriginSubLocation", name: "SampleOriginSubLocation", value: tmpSampleOriginSubLocation},
															{externalName: "SampleCollectionDateTime", name: "SampleCollectionDateTime", value: tmpSampleCollectionDateTime},
															{externalName: "SampleCollectionMethod", name: "SampleCollectionMethod", value: tmpSampleCollectionMethod},
															{externalName: "TestSiteSampleReceivedDateTime", name: "TestSiteSampleReceivedDateTime", value: tmpTestSiteSampleReceivedDateTime},
															{externalName: "TestPriority", name: "TestPriority", value: tmpTestPriority},
															{externalName: "TestRequestSite", name: "TestRequestSite", value: tmpTestRequestSite},
															{externalName: "TestSite", name: "TestSite", value: tmpTestSite},
															{externalName: "TestType", name: "TestType", value: tmpTestType},
															{externalName: "TestOrderedDateTime", name: "TestOrderedDateTime", value: tmpTestOrderedDateTime}]});
				}

				currEpisodeID		= tmpEpisodeID;
				currStatus		= sqlResult.getString("Processed_YN");

				tmpTestLISCode		= sqlResult.getString("TestLISCode");
				tmpTestLISName		= sqlResult.getString("TestLISName");
				tmpTestValue		= sqlResult.getString("TestValue");
				tmpTestNormalRange	= sqlResult.getString("TestNormalRange");
				tmpTestUnits		= sqlResult.getString("TestUnits");
				// batchSeq is only used when processing the records with a status of "N"
				// well actually the records will have a status of "P" for processing
				// but they haven't already beenprocessed
				batchSeq					= sqlResult.getInt("BatchSeq");

				episodes[episodeSize - 1].attributeWithValues.push({externalName: tmpTestLISCode, name: tmpTestLISName, value: tmpTestValue,
														units: tmpTestUnits, range: tmpTestNormalRange});

				/* For testing only. The API fails when sending the acxtual tests from the incoming messages*/
				//episodes[episodeSize - 1].attributeWithValues.push({externalName: "GTT 120m", name: "GTTP 120min", value: "6.7"});

				if (tmpPatientID != currPatientID){
					currPatientID = tmpPatientID;
				}
			} //end while
			if (currStatus == "N") {
				var currPatient = {"caseId": currPatientID , site: "", episodes: []};
				currPatient.episodes = episodes;
				var currMessage = {"batchSeq": batchSeq, "episodeID": currEpisodeID, Patient: currPatient};

				var json = JSON.stringify(currPatient);
				// Send the JSON Object to the Web Service
				logger.trace("JSON Routing to REST CLIENT: " + json);
				resp = router.routeMessage("Rest Client 3", JSON.stringify(currMessage));
				if (resp) {
					logger.trace("Response from REST Channel: " + resp.getMessage());
				}
			}
			return true;
		}
	}
	catch(ex) {
		logger.error("PKS - Select_CDS_Data - Exception: " + ex);
		throw ex;
	}
	finally {
	//	dbConn.close();
	}

}

function Select_CDS_Data4() {
	var currEpisodeID = "";
	var currEpisode;
	var currPatientID = "";
	var currStatus = "";
	var episodes = [];
	var episodeSize = 0;
	var resp;

	var dbConn = Get_CDS_Connection();
	try {
		var sqlQuery = "call rd_staging.rdSelCDSData()";

		var sqlResult = dbConn.executeCachedQuery(sqlQuery);
		// We can assume there will always be at least one row, even if there are no records to process
		// Let's check the result set
		var colCount = sqlResult.getMetaData().getColumnCount();
		// We're checking for the column count, if it is 2 then it's just a return code and message

		if (colCount == 2) {
			sqlResult.next();
			logger.trace("PKS - Select_CDS_Data - Info: " + sqlResult.getString("Message"));
			return false;
		} else {
			while (sqlResult.next()) {
				tmpEpisodeID		               = sqlResult.getString("EpisodeID");
				tmpEpisodeOrderedTests             = sqlResult.getString("EpisodeOrderedTests");
				tmpPatientID		               = sqlResult.getString("PatientID");
				tmpPatientSurname	               = sqlResult.getString("PatientFamilyName");
				tmpEpisodeDateTime   	          = sqlResult.getString("EpisodeDateTime");
				tmpPatientDOB		               = sqlResult.getString("PatientDOB");
				tmpTestSite		               = sqlResult.getString("TestSite");
				tmpPatientFamilyName               = sqlResult.getString("PatientFamilyName");
				tmpPatientGivenName                = sqlResult.getString("PatientGivenName");
				tmpPatientTitle                    = sqlResult.getString("PatientTitle");
				tmpPatientSex                      = sqlResult.getString("PatientSex");
				tmpPatientStreetAddress            = sqlResult.getString("PatientStreetAddress");
				tmpPatientCity                     = sqlResult.getString("PatientCity");
				tmpPatientPostalCode               = sqlResult.getString("PatientPostalCode");
				tmpPatientState                    = sqlResult.getString("PatientState");
				tmpPatientCountry                  = sqlResult.getString("PatientCountry");
				tmpPatientStatus                   = sqlResult.getString("PatientStatus");
				tmpPatientPregnancyFlag            = sqlResult.getString("PatientPregnancyFlag");
				tmpPatientFastingFlag              = sqlResult.getString("PatientFastingFlag");
				tmpEpisodeClinicalNotes            = sqlResult.getString("EpisodeClinicalNotes");
				tmpAccountType                     = sqlResult.getString("AccountType");
				tmpAccountClass                    = sqlResult.getString("AccountClass");
				tmpBillTo                          = sqlResult.getString("BillTo");
				tmpBillingAddress                  = sqlResult.getString("BillingAddress");
				tmpPatientPublicHealthIdentifier   = sqlResult.getString("PatientPublicHealthIdentifier");
				tmpEpisodePublicScheduleCode       = sqlResult.getString("EpisodePublicScheduleCode");
				tmpPatientPrivateHealthFundName    = sqlResult.getString("PatientPrivateHealthFundName");
				tmpPatientPrivateHealthFundID      = sqlResult.getString("PatientPrivateHealthFundID");
				tmpReqPhysicianID                  = sqlResult.getString("ReqPhysicianID");
				tmpReqPhysicianName                = sqlResult.getString("ReqPhysicianName");
				tmpReqPhysicianSpeciality          = sqlResult.getString("ReqPhysicianSpeciality");
				tmpReqPhysicianDeliveryAddress     = sqlResult.getString("ReqPhysicianDeliveryAddress");
				tmpSugeryName                      = sqlResult.getString("SugeryName");
				tmpSurgeryCode                     = sqlResult.getString("SurgeryCode");
				tmpSurgeryAddress                  = sqlResult.getString("SurgeryAddress");
				tmpSurgeryPostalCode               = sqlResult.getString("SurgeryPostalCode");
				tmpCopyPhysicianIDList             = sqlResult.getString("CopyPhysicianIDList");
				tmpCopyPhysicianNameList           = sqlResult.getString("CopyPhysicianNameList");
				tmpPanelIndicator                  = sqlResult.getString("PanelIndicator");
				tmpPanelID                         = sqlResult.getString("PanelID");
				tmpPanelName                       = sqlResult.getString("PanelName");
				tmpSampleID                        = sqlResult.getString("SampleID");
				tmpSampleFluidType                 = sqlResult.getString("SampleFluidType");
				tmpSampleOriginLocation            = sqlResult.getString("SampleOriginLocation");
				tmpSampleOriginSubLocation         = sqlResult.getString("SampleOriginSubLocation");
				tmpSampleCollectionDateTime        = sqlResult.getString("SampleCollectionDateTime");
				tmpSampleCollectionMethod          = sqlResult.getString("SampleCollectionMethod");
				tmpTestSiteSampleReceivedDateTime  = sqlResult.getString("TestSiteSampleReceivedDateTime");
				tmpTestPriority                    = sqlResult.getString("TestPriority");
				tmpTestDepartment                  = sqlResult.getString("TestDepartment");
				tmpTestInstrument                  = sqlResult.getString("TestInstrument");
				tmpTestRequestSite                 = sqlResult.getString("TestRequestSite");
				tmpTestSite                        = sqlResult.getString("TestSite");
				tmpTestType                        = sqlResult.getString("TestType");
				tmpAbnormalFlag                    = sqlResult.getString("AbnormalFlag");
				tmpTestOrderedDateTime             = sqlResult.getString("TestOrderedDateTime");
				tmpTestAnalyzerCompletionDateTime  = sqlResult.getString("TestAnalyzerCompletionDateTime");
				tmpTestResultFirstReviewedDateTime = sqlResult.getString("TestResultFirstReviewedDateTime");
				tmpTestResultReleasedDateTime      = sqlResult.getString("TestResultReleasedDateTime");
				tmpEpisodeDate		= DateUtil.getDate("yyyy-MM-dd HH:mm:ss", tmpEpisodeDateTime).getTime();

				// Check whether we moved onto a different episode, if we did and it hadn't already been processed then send a new
				// case...
				if ((tmpEpisodeID != currEpisodeID)) {
					if ($('cases_from_most_recent_episode_only') != 'true') {
						if (currEpisodeID != "") {
							if (currStatus == "N") {
								var currPatient = {"caseId": currEpisodeID , site: "", episodes: []};
								currPatient.episodes = episodes;
								// We're putting the batchSeq into the JSON message, the destination channel can split this out for use later
								// This is used for updating the records once the records are successfully sent to the web service
								var currMessage = {"batchSeq": batchSeq, "episodeID": currEpisodeID, Patient: currPatient};

								var json = JSON.stringify(currPatient);
								logger.trace("JSON Routing to REST CLIENT: " + json);
								// Send the JSON Object to the Web Service channel
								resp = router.routeMessage("Rest Client", JSON.stringify(currMessage));
								if (resp) {
									logger.trace("Response from REST Channel: " + resp.getMessage());
								}
							}
						}
					}

					episodeSize = episodes.push({episodeDate: tmpEpisodeDate,
											episodeId: tmpEpisodeID,
											attributeWithValues: [{externalName: "PID", name: "PID", value: tmpPatientID},
															{externalName: "BIRTHDATE", name: "BIRTHDATE", value: tmpPatientDOB},
															{externalName: "LASTNAME", name: "LASTNAME", value: tmpPatientSurname},
															{externalName: "EpisodeOrderedTests", name: "EpisodeOrderedTests", value: tmpEpisodeOrderedTests},
															{externalName: "Site", name: "Site", value: tmpTestSite},
															{externalName: "PatientFamilyName", name : "PatientFamilyName", value: tmpPatientFamilyName},
															{externalName: "PatientGivenName", name : "PatientGivenName", value: tmpPatientGivenName},
															{externalName: "PatientTitle", name : "PatientTitle", value: tmpPatientTitle},
															{externalName: "PatientSex", name : "PatientSex", value: tmpPatientSex},
															{externalName: "PatientStreetAddress", name : "PatientStreetAddress", value: tmpPatientStreetAddress},
															{externalName: "PatientCity", name : "PatientCity", value: tmpPatientCity},
															{externalName: "PatientPostalCode", name : "PatientPostalCode", value: tmpPatientPostalCode},
															{externalName: "PatientState", name : "PatientState", value: tmpPatientState},
															{externalName: "PatientCountry", name : "PatientCountry", value: tmpPatientCountry},
															{externalName: "PatientStatus", name : "PatientStatus", value: tmpPatientStatus},
															{externalName: "PatientPregnancyFlag", name : "PatientPregnancyFlag", value: tmpPatientPregnancyFlag},
															{externalName: "PatientFastingFlag", name : "PatientFastingFlag", value: tmpPatientFastingFlag}	,
															{externalName: "PatientGivenName", name : "externalName", value: tmpPatientGivenName},
															{externalName: "EpisodeClinicalNotes", name: "EpisodeClinicalNotes", value: tmpEpisodeClinicalNotes},
															{externalName: "AccountType", name: "AccountType", value: tmpAccountType},
															{externalName: "AccountClass", name: "AccountClass", value: tmpAccountClass},
															{externalName: "BillTo", name: "BillTo", value: tmpBillTo},
															{externalName: "PatientPublicHealthIdentifier", name: "PatientPublicHealthIdentifier", value: tmpPatientPublicHealthIdentifier},
															{externalName: "EpisodePublicScheduleCode", name: "EpisodePublicScheduleCode", value: tmpEpisodePublicScheduleCode},
															{externalName: "PatientPrivateHealthFundName", name: "PatientPrivateHealthFundName", value: tmpPatientPrivateHealthFundName},
															{externalName: "PatientPrivateHealthFundID", name: "PatientPrivateHealthFundID", value: tmpPatientPrivateHealthFundID},
															{externalName: "ReqPhysicianID", name: "ReqPhysicianID", value: tmpReqPhysicianID},
															{externalName: "ReqPhysicianName", name: "ReqPhysicianName", value: tmpReqPhysicianName},
															{externalName: "ReqPhysicianSpeciality", name: "ReqPhysicianSpeciality", value: tmpReqPhysicianSpeciality},
															{externalName: "SugeryName", name: "SugeryName", value: tmpSugeryName},
															{externalName: "SurgeryCode", name: "SurgeryCode", value: tmpSurgeryCode},
															{externalName: "CopyPhysicianIDList", name: "CopyPhysicianIDList", value: tmpCopyPhysicianIDList},
															{externalName: "CopyPhysicianNameList", name: "CopyPhysicianNameList", value: tmpCopyPhysicianNameList},
															{externalName: "PanelIndicator", name: "PanelIndicator", value: tmpPanelIndicator},
															{externalName: "PanelID", name: "PanelID", value: tmpPanelID},
															{externalName: "PanelName", name: "PanelName", value: tmpPanelName},
															{externalName: "SampleID", name: "SampleID", value: tmpSampleID},
															{externalName: "SampleFluidType", name: "SampleFluidType", value: tmpSampleFluidType},
															{externalName: "SampleOriginLocation", name: "SampleOriginLocation", value: tmpSampleOriginLocation},
															{externalName: "SampleOriginSubLocation", name: "SampleOriginSubLocation", value: tmpSampleOriginSubLocation},
															{externalName: "SampleCollectionDateTime", name: "SampleCollectionDateTime", value: tmpSampleCollectionDateTime},
															{externalName: "SampleCollectionMethod", name: "SampleCollectionMethod", value: tmpSampleCollectionMethod},
															{externalName: "TestSiteSampleReceivedDateTime", name: "TestSiteSampleReceivedDateTime", value: tmpTestSiteSampleReceivedDateTime},
															{externalName: "TestPriority", name: "TestPriority", value: tmpTestPriority},
															{externalName: "TestRequestSite", name: "TestRequestSite", value: tmpTestRequestSite},
															{externalName: "TestSite", name: "TestSite", value: tmpTestSite},
															{externalName: "TestType", name: "TestType", value: tmpTestType},
															{externalName: "TestOrderedDateTime", name: "TestOrderedDateTime", value: tmpTestOrderedDateTime}]});
				}

				currEpisodeID		= tmpEpisodeID;
				currStatus		= sqlResult.getString("Processed_YN");

				tmpTestLISCode		= sqlResult.getString("TestLISCode");
				tmpTestLISName		= sqlResult.getString("TestLISName");
				tmpTestValue		= sqlResult.getString("TestValue");
				tmpTestNormalRange	= sqlResult.getString("TestNormalRange");
				tmpTestUnits		= sqlResult.getString("TestUnits");
				// batchSeq is only used when processing the records with a status of "N"
				// well actually the records will have a status of "P" for processing
				// but they haven't already beenprocessed
				batchSeq					= sqlResult.getInt("BatchSeq");

				episodes[episodeSize - 1].attributeWithValues.push({externalName: tmpTestLISCode, name: tmpTestLISName, value: tmpTestValue,
														units: tmpTestUnits, range: tmpTestNormalRange});

				/* For testing only. The API fails when sending the acxtual tests from the incoming messages*/
				//episodes[episodeSize - 1].attributeWithValues.push({externalName: "GTT 120m", name: "GTTP 120min", value: "6.7"});

				if (tmpPatientID != currPatientID){
					currPatientID = tmpPatientID;
				}
			} //end while
			if (currStatus == "N") {
				var currPatient = {"caseId": currPatientID , site: "", episodes: []};
				currPatient.episodes = episodes;
				var currMessage = {"batchSeq": batchSeq, "episodeID": currEpisodeID, Patient: currPatient};

				var json = JSON.stringify(currPatient);
				// Send the JSON Object to the Web Service
				logger.trace("JSON Routing to REST CLIENT: " + json);
				resp = router.routeMessage("Rest Client 4", JSON.stringify(currMessage));
				if (resp) {
					logger.trace("Response from REST Channel: " + resp.getMessage());
				}
			}
			return true;
		}
	}
	catch(ex) {
		logger.error("PKS - Select_CDS_Data - Exception: " + ex);
		throw ex;
	}
	finally {
	//	dbConn.close();
	}

}

